<?phpnamespace App\Http\Requests;use Illuminate\Foundation\Http\FormRequest;class AdminReportsRequest extends FormRequest{    public function rules()    {        return [            'category_id' => 'required',            'username' => 'required',            'UID' => 'required',            'fullname' => 'required',            'numberbank' => 'required:regex:/^0\d{9}$/',            'namebank' => 'required',            'moneys_scam' => 'required',            'content' => 'required',            'numberphone' => 'required|regex:/^0\d{9}$/',            'images' => 'required',            'status' => 'required',        ];    }    public function messages()    {        return        [            'numberbank.regex' => 'Số tài khoản phải đúng định dạng',            'numberphone.regex'=> "Số điện thoại phải đúng định dạng"        ];    }}