<?phpnamespace App\Http\Requests;use Illuminate\Foundation\Http\FormRequest;class StoreBannerRequest extends FormRequest{    /**     * Get the validation rules that apply to the request.     *     * @return array<string, mixed>     */    public function rules()    {        return [            'images'=> ['required']        ];    }    public function messages()    {        return [          'images.required' => 'Ảnh banner không được để trống'        ];    }}